# the Season is the year, of the game except when the game's in January. In those cases its the year -1

logs &lt;- logs |&gt; mutate(Season = case_when(
  month(Date) == 1 ~ year(Date) -1,
  .default = year(Date)
))  

season_games &lt;- logs |&gt; 
  group_by(Season) |&gt;  
  summarise(all_games =n())

season_games_under_100 &lt;- logs |&gt; 
  filter(Result == "W", RushYds &lt; 100) |&gt; 
  group_by(Season) |&gt;  
  summarise(low_rush_wins =n()) 

season_games &lt;- season_games |&gt;  
  inner_join(season_games_under_100, join_by(Season)) |&gt; 
  mutate(pct_low_rush_wins = low_rush_wins/all_games* 100)




