ggplot(btnvsnational_long, aes(x = Season, y = Percent, color = Category)) +
  geom_line(linewidth = 1.2) +
  geom_point(size = 2) +
   scale_color_manual(   values = c(
      "pct_low_rush_wins" = "black", 
      "btnpct_low_rush_wins" = "blue"
    ), labels=c(
      "pct_low_rush_wins" = "National Percentage",
      "btnpct_low_rush_wins" = "Big Ten Percentage"
    )
    )+
  labs(
    title = "CFB Games where the winning team rushed for under 100 yards",
    subtitle = "2021 saw all time highs in the conference and nationally",
    x = "Year",
    y = "Low Rush Win Percentage",
    color = "Legend" 
  ) + 
  scale_x_continuous(breaks = unique(btnvsnational_long$Season)) +
